  function Swall_Delete_Guru(guru_id){
  //  var kd_kary=kd_kar;
    // alert('error hapus'+kd_kary);
    var base_url = $('#base').val();
    console.log(base_url);
    var base_url2=base_url+'Guru/';
    swal({
      title: "Anda yakin?",
      text: "Data yang sudah terhapus tidak bisa dikembalikan lagi !",
      icon: "warning",
      buttons: true,
      dangerMode: true,

    })
    .then((willDelete) => {
      if (willDelete) {
          $.ajax({
            url:base_url2+"do_delete",
            type:"POST",
            data:{'guru_id':guru_id},

            success:function(){
              swal("Terhapus!", "Data Berhasil Dihapus.", {
                icon: "success",
              }).then((value) => {
                  window.location.reload();
                });

            },
            error:function(){
              alert('error hapus');
            }

          });

      } else {
        swal("Cancelled", "Data Tidak Jadi Dihapus");
      }
    });
    
    }
