// untuk pop upnya


  function _edit_guru(guru_id){
    // untuk menampung isi data dari guri id
    var datam={"guru_id":guru_id};
    var base_url = $('#base').val();
    var base_url2=base_url+'Guru/';


    // ajax
    jQuery.ajax({
      // url untuk menuju data yang akan di kirim
      url: base_url2+"edit_data",
      method : "POST",
      data:datam,
      success:function(data){

        jQuery('body').append(data);
        // untuk menampilkan modal berdasarkan edit
        jQuery('#myModaledit').modal('toggle');
        // untuk menghilangkan isi post agar kembali menjadi 0 pada variabel datam
        jQuery('#myModaledit').remove();
      },
      error:function(){
          // alert("something went wrong"+datam).console.error();
          swal ( "Oops" ,  "Something went wrong!" ,  "error" );
      }

    });
  }
  function closeModal(){
    // untuk merefresh halaman
     window.location.reload();

  }

  function Swall_Delete_Guru(guru_id){
  //  var kd_kary=kd_kar;
    // alert('error hapus'+kd_kary);
    var base_url = $('#base').val();
    console.log(base_url);
    var base_url2=base_url+'Guru/';
    swal({
      title: "Anda yakin?",
      text: "Data yang sudah terhapus tidak bisa dikembalikan lagi !",
      icon: "warning",
      buttons: true,
      dangerMode: true,

    })
    .then((willDelete) => {
      if (willDelete) {
          $.ajax({
            url:base_url2+"do_delete",
            type:"POST",
            data:{'guru_id':guru_id},

            success:function(){
              swal("Terhapus!", "Data Berhasil Dihapus.", {
                icon: "success",
              }).then((value) => {
                  window.location.reload();
                });

            },
            error:function(){
              alert('error hapus');
            }

          });

      } else {
        swal("Cancelled", "Data Tidak Jadi Dihapus");
      }
    });

    }
